            item1 = types.InlineKeyboardButton("–ê–¥–∞–º –±–∏–æ–ª–æ–≥–∏—è—Å—ã", callback_data='biol')
            item2 = types.InlineKeyboardButton("–ê–¥–∞–º–Ω—ã“£ –ø–∞–π–¥–∞ –±–æ–ª—É—ã", callback_data='hist')
